  expand_grid(select(sp_ebird, "species_code", "id", "english")) |&gt;
  mutate(in_range = FALSE)

recvs |&gt;
  st_drop_geometry() |&gt;
  full_join(p, by = c("deployID", "deviceID", "name", "species_code", 
                      "id", "english", "in_range")) |&gt;
